import React, { useState, useEffect } from 'react';
import io from 'socket.io-client';
import './App.scss';

// Importing Material-UI Icons
import DirectionsWalkIcon from '@material-ui/icons/DirectionsWalk';
import WarningIcon from '@material-ui/icons/Warning';
import FitnessCenterIcon from '@material-ui/icons/FitnessCenter';
import BatteryFullIcon from '@material-ui/icons/BatteryFull';

// Initialize socket connection to the backend
const socket = io.connect('http://localhost:5000');

// Function to capitalize the first letter
function capitalizeFirstLetter(string) {
  return string.charAt(0).toUpperCase() + string.slice(1);
}

function App() {
  const [stepCount, setStepCount] = useState(0);
  const [fallDetected, setFallDetected] = useState(false);
  const [movementType, setMovementType] = useState('walking');
  const [batteryLevel, setBatteryLevel] = useState(100);

  useEffect(() => {
    // Listen for updates from the backend
    socket.on('sensorData', (data) => {
      setStepCount(data.stepCount);
      setFallDetected(data.isFallDetected);
      setMovementType(data.movementType);
      setBatteryLevel(data.batteryLevel);
    });
  }, []);

  return (
    <div className="App">
      <h1>Patient Monitoring Dashboard</h1>
      <div className="dashboard">
        <div className="dashboard-item">
          <DirectionsWalkIcon style={{ fontSize: 40, color: "white" }} />
          <h3>Step Count</h3>
          <p>{stepCount}</p>
        </div>
        <div className="dashboard-item">
          <WarningIcon style={{ fontSize: 40, color: fallDetected ? "red" : "white" }} />
          <h3>Fall Detection</h3>
          <p>{fallDetected ? "Fall Detected!" : "No Falls"}</p>
        </div>
        <div className="dashboard-item">
          <FitnessCenterIcon style={{ fontSize: 40, color: "white" }} />
          <h3>Movement Classification</h3>
          <p>{capitalizeFirstLetter(movementType)}</p> {/* Capitalizes the first letter */}
        </div>
        <div className="dashboard-item">
          <BatteryFullIcon style={{ fontSize: 40, color: "white" }} />
          <h3>Battery Level</h3>
          <p>{batteryLevel}%</p>
        </div>
      </div>
    </div>
  );
}

export default App;
