import React, { useEffect, useState } from 'react';
import './NavigationBar.scss'; // Add styles for the NavigationBar
import AccountCircleIcon from '@material-ui/icons/AccountCircle';

const NavigationBar = () => {
  const [currentDateTime, setCurrentDateTime] = useState(new Date().toLocaleString());

  useEffect(() => {
    // Update the time every second
    const intervalId = setInterval(() => {
      setCurrentDateTime(new Date().toLocaleString());
    }, 1000); // Update every 1000 milliseconds (1 second)

    // Clear the interval on component unmount
    return () => clearInterval(intervalId);
  }, []);

  const userName = "John Doe"; // Replace with actual user's name
  const userPosition = "Patient"; // Replace with actual user position

  return (
    <div className="navbar">
      <div className="user-info">
        <AccountCircleIcon style={{ fontSize: 40, color: "white" }} />
        <div>
          <h4>{userName}</h4>
          <p>{userPosition}</p>
        </div>
      </div>
      <div className="current-time">
        <h4>{currentDateTime}</h4>
      </div>
      <button className="signout-button">Sign Out</button>
    </div>
  );
};

export default NavigationBar;
